import React, { Component } from "react";
import StudentDataService from "../Services/StudentService";
import { Link } from "react-router-dom";

  export default class RejectedApplicantList extends Component {
    constructor(props) {
      super(props);
      this.approveApplicant = this.approveApplicant.bind(this);
      
      this.state = {
        applicants: []
      };
    }

  componentDidMount() {
    StudentDataService.getBlockedApplicants().then((res) =>{
      this.setState({applicants:res.data});
    });
  }

  approveApplicant(id){

    StudentDataService.updateApplicant(
      id,"Approved"            
  )
      .then(response => {
          console.log(response.data);
          //this.props.history.push('ApprovedReviewList')
          window.location.reload();
      })
      .catch(e => {
          console.log(e);
      });
  }

  render(){
    const{reviews} = this.state;
    return(
      <div>
        <h2 className="text-center">Blocked Applicant List</h2>
        <div className="row">
          <table className = "table table-striped table-bordered">
            <thead>
              <tr>
                <th>Applicant ID</th>
                <th>Applicant Name</th>
                <th>Applicant Reviews</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {
                this.state.applicants.map(user => 
                <tr key = {user.id}>
                  <td>{user.id}</td>
                  <td>{user.firstName} {user.lastName}</td>
                  <td>{user.reviews.map(r =>
                    <div key = {r.id}>
                      <div>{r.id}</div>
                      <div>{r.reviewDescription}</div>
                      </div>)}
                      </td>
                  <td><button className="btn btn-primary" type = "button" onClick = {(e)=>this.approveApplicant(user.id)}>Approve</button></td>
                </tr>  
                  )
              }
            </tbody>
  
          </table>
        </div>
  
      </div>
    );
  
  }

}
