import React, { Component } from "react";
import DataService from "../Services/WebUserJobService";
import { Link } from "react-router-dom";

  export default class AllJobsList extends Component {
    constructor(props) {
      super(props);
      
      this.state = {
        categories: []
      };
    }

  componentDidMount() {
    DataService.getJobCategory().then((res) =>{
      this.setState({jobs:res.data});
    });
  }

  render(){
    const{categories} = this.state;
    return(
      <div>
        <h2 className="text-center">Jobs per Category</h2>
        <div className="row">

        <div>
            {this.state.categories.map(txt => <p>{txt}</p>)}
        </div>
    
        </div>
  
      </div>
    );
  
  }

}
