import React, { Component } from "react";
import StudentDataService from "../Services/StudentService";
import { Link } from "react-router-dom";

export default class ReviewList extends Component {
  constructor(props) {
    super(props);
    this.retrieveReviews = this.retrieveReviews.bind(this);
    this.refreshReviewLIst = this.refreshReviewList.bind(this);
    this.setActiveReview = this.setActive.bind(this);    

    this.state = {
      reviews: [],
      currentReview: null,
      currentIndex: -1,
    };
  }

  componentDidMount() {
    this.retrieveReviews();
  }


  retrieveStudents() {
    StudentDataService.getReviews()
      .then(response => {
        this.setState({
          reviews: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshReviewList() {
    this.retrieveReviews();
    this.setState({
      currentReview: null,
      currentIndex: -1
    });
  }

  setActiveReview(review, index) {
    this.setState({
      currentReview: review,
      currentIndex: index
    });
  }

  render() {
    const { reviews, currentReview, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-6">
          <h4>Students List</h4>

          <ul className="list-group">
            {reviews &&
              reviews.map((review, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveReview(review, index)}
                  key={index}
                >
                  {review.id} {review.reviewstars} {review.reviewDescription}{review.reviewDate} {review.reviewDescription}{review.company}{review.job}{review.applicant}
                </li>
              ))}
          </ul>
        </div>
        <div className="col-md-6">
          {currentReview ? (
            <div>
              <h4>Review</h4>
              <div>
                <label>
                  <strong>ID:</strong>
                </label>{" "}
                {currentReview.id}
              </div>
              <div>
                <label>
                  <strong>Stars:</strong>
                </label>{" "}
                {currentReview.reviewstars}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentReview.reviewDescription}
              </div>
              <div>
                <label>
                  <strong>Date:</strong>
                </label>{" "}
                {currentReview.reviewDate}
              </div>
              <div>
                <label>
                  <strong>Company:</strong>
                </label>{" "}
                {currentReview.company.name}
              </div>
              <div>
                <label>
                  <strong>Job Position:</strong>
                </label>{" "}
                {currentReview.job.jobTitle}
              </div>
              <div>
                <label>
                  <strong>Applicant:</strong>
                </label>{" "}
                {currentReview.applicant.firstName}
              </div>
              <Link
                to={"/reviews/" + currentReview.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Review...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
