import React, { Component } from "react";
import StudentDataService from "../Services/StudentService";
import { Link } from "react-router-dom";

  export default class ReviewList extends Component {
    constructor(props) {
      super(props);
      this.updateStudent = this.updateStudent.bind(this);
      
      this.state = {
        reviews: []
      };
    }

  componentDidMount() {
    StudentDataService.getReviews().then((res) =>{
      this.setState({reviews:res.data});
    });
  }

  approveReview(id){

    this.setState(prevState => ({

      reviews: prevState.reviews.map(
        el => el.id === id? { ...el, reviewStatus: 'Approved' }: el
      )
    }))

    StudentService.updateReview(
      id,"Approved"            
  )
      .then(response => {
          console.log(response.data);
          this.setState({
              message: "The review was updated successfully!"
          });
      })
      .catch(e => {
          console.log(e);
      });
  }

  render(){
    const{reviews} = this.state;
    return(
      <div>
        <h2 className="text-center">Review List</h2>
        <div className="row">
          <table className = "table table-striped table-bordered">
            <thead>
              <tr>
                <th>Review ID</th>
                <th>Review Stars</th>
                <th>Review Description</th>
                <th>Review Date</th>
                <th>Review User</th>
                <th>Review Actions</th>
              </tr>
            </thead>
            <tbody>
              {
                this.state.reviews.map(review => 
                <tr key = {review.id}>
                  <td>{review.id}</td>
                  <td>{review.reviewstars}</td>
                  <td>{review.reviewDescription}</td>
                  <td>{review.reviewDate}</td>
                  <td>{review.reviewStatus}</td>
                  <td><button className="btn btn-primary" type = "button" onClick = {(e)=>this.approveReview(review.id)}>Approve</button></td>
                </tr>  
                  )
              }
            </tbody>
  
          </table>
        </div>
  
      </div>
    );
  
  }

}
