import React, { Component } from "react";
import DataService from "../Services/WebUserJobService";
import { Link } from "react-router-dom";

  export default class AllJobsList extends Component {
    constructor(props) {
      super(props);
      
      this.state = {
        categories: []
      };
    }

  componentDidMount() {
    DataService.getJobCategory().then((res) =>{
      this.setState({categories:res.data});
    });
  }

  jobCategory(id){

    DataService.getJobIndustry(
      id          
  )
      .then(response => {
          console.log(response.data);
          this.props.history.push('AllViewedJobsList')
          //window.location.reload();
      })
      .catch(e => {
          console.log(e);
      });
  }

  render(){
    const{categories} = this.state;
    return(
      <div>
        <h2 className="text-center">Jobs per Category</h2>
        <div className="row">
        <div>
      {
        Object.keys(this.state.categories).map((key, i) => (
          <p key={i}>
            <span>Key Name: {key}</span>
            <span>Value: {this.state.categories[key]}</span>
          </p>
        ))
      }
    </div>
    
        </div>
  
      </div>
    );
  
  }

}
